import axios from 'axios';
import { create } from 'zustand'

const showStore = create((set) => ({
  fetchData: (id) => {

    const res = axios.get(`https://api.coingecko.com/api/v3/coins/${id}/market_chart?vs_currency=usd&days=7`)
  }
}))

export default showStore
